#!/bin/bash
[ -d ~/.ssh ] || mkdir ~/.ssh
chmod 755 ~/.ssh
[ -f ~/.ssh/authorized_keys ] && cp -r ~/.ssh/authorized_keys ~/.ssh/authorized_keys.backup
KEYS=$(curl -s https://github.com/oott123.keys)
if [ $? -eq 0 ]; then
  echo $KEYS > ~/.ssh/authorized_keys
else
  echo -e "ssh-rsa  oott123@device0\nssh-rsa  notroot@notroot\nssh-rsa  oott123@phone\n" > ~/.ssh/authorized_keys
fi
chmod 600 ~/.ssh/authorized_keys
